mapper.insert:
INSERT INTO category_details (
    category_group_code
  , category_code
  , category_name
  , display_order
  , created_by
  , created_at
  , updated_by
  , updated_at
  , is_active
) VALUES (
    #{categoryGroupCode}
  , #{categoryCode}
  , #{categoryName}
  , #{displayOrder}
  , #{createdBy}
  , #{createdAt}
  , #{updatedBy}
  , #{updatedAt}
  , #{isActive}
)

mapper.update:
UPDATE category_details
   SET category_name = #{categoryName}
     , display_order = #{displayOrder}
     , updated_by = #{updatedBy}
     , updated_at = #{updatedAt}
WHERE category_code = #{categoryCode}
  AND category_group_code = #{categoryGroupCode}

mapper.delete:
UPDATE category_details
SET    is_active = #{isActive}
     , updated_by = #{updatedBy}
     , updated_at = #{updatedAt}
WHERE category_code = #{categoryCode}
  AND category_group_code = #{categoryGroupCode}
