mapper.select:
SELECT COUNT(*)
  FROM returns
 WHERE order_no = #{requestNo}
   AND customer_id = #{refundNumber}

SELECT return_id
  FROM returns
 WHERE order_no = #{requestNo}
   AND customer_id = #{refundNumber}

mapper.update:
UPDATE returns
SET    reason = #{reason}
     , total_amount = #{totalAmount}
     , refund_amount = #{refundAmount}
     , return_status_value = #{returnStatusValue}

     , shipping_fee_customer_amount = #{shippingFeeCustomerAmount}


     , shipping_fee_seller_amount = #{shippingFeeSellerAmount}

     , updated_by = #{updatedBy}
     , updated_at = #{updatedAt}
WHERE order_no = #{orderNo}
AND   customer_id  = #{customerId}

mapper.insert:
INSERT INTO return_details (
      return_id
    , product_code
    , quantity
    , unit_price
    , is_active
    , created_by
    , created_at
    , updated_by
    , updated_at
) VALUES (
      #{returnId}
    , #{productCode}
    , #{quantity}
    , #{unitPrice}
    , 1
    , #{createdBy}
    , #{createdAt}
    , #{updatedBy}
    , #{updatedAt}
)
